import React from "react";
import {
  CButton,
  CCard,
  CCardBody,
  CCardFooter,
  CCol,
  CContainer,
  CForm,
  CRow,
} from "@coreui/react";
import RInput from "src/Components/UI/Register/RInput";
import RButton from "src/Components/UI/Register/RButton";
import useFormValidate from "src/Hooks/input-validation";
import Error from "src/Components/UI/Error/Error";

const Register = () => {
  const {
    inputValue: fname,
    setInputValue: setFname,
    inputIsTouched: fnameIsTouched,
    inputChangeHandler: fnameChangeHandler,
    inputBlurHandler: fnameBlurHandler,
  } = useFormValidate();
  const {
    inputValue: lname,
    setInputValue: setLname,
    inputIsTouched: lnameIsTouched,
    inputChangeHandler: lnameChangeHandler,
    inputBlurHandler: lnameBlurHandler,
  } = useFormValidate();
  const {
    inputValue: Username,
    setInputValue: setUsername,
    inputIsTouched: usernameIsTouched,
    inputChangeHandler: usernameChangeHandler,
    inputBlurHandler: usernameBlurHandler,
  } = useFormValidate();

  let isFormValid = false;
  const fnameIsValid = fname.trim() !== "";
  const fnameIsInvalid = !fnameIsValid && fnameIsTouched;

  const lnameIsValid = lname.trim() !== "";
  const lnameIsInvalid = !lnameIsValid && lnameIsTouched;

  const usernameIsValid = username.trim() !== "";
  const usernameIsInvalid = !usernameIsInvalid && usernameIsTouched;

  if (fnameIsValid && lnameIsInvalid && usernameIsValid) {
    isFormValid = true;
  }
  return (
    <div className="c-app c-default-layout flex-row align-items-center">
      <CContainer>
        <CRow className="justify-content-center">
          <CCol md="9" lg="7" xl="6">
            <CCard className="mx-4">
              <CCardBody className="p-4">
                <CForm>
                  <h1>Register</h1>
                  <p className="text-muted">Create your account</p>
                  <RInput
                    class="mb-3"
                    iconName="cil-user"
                    name="First Name"
                    auto="firstname"
                    icon={true}
                    value={fname}
                    change={fnameChangeHandler}
                    blur={fnameBlurHandler}
                  />
                  {fnameIsInvalid && <Error error="First name is required" />}
                  <RInput
                    class="mb-3"
                    iconName="cil-user"
                    name="Last Name"
                    auto="lastname"
                    icon={true}
                    value={lname}
                    change={lnameChangeHandler}
                    blur={lnameBlurHandler}
                  />
                  {lnameIsInvalid && <Error error="Last name is required" />}
                  <RInput
                    class="mb-3"
                    iconName="cil-user"
                    name="Username"
                    auto="username"
                    icon={true}
                    value={username}
                    change={usernameChangeHandler}
                    blur={usernameBlurHandler}
                  />
                  {username && <Error error="Last name is required" />}
                  <RInput
                    class="mb-3"
                    iconName="@"
                    name="Email Address"
                    auto="email"
                    icon={false}
                  />
                  <RInput
                    class="mb-3"
                    iconName="cil-lock-locked"
                    name="Password"
                    auto="password"
                    icon={true}
                  />
                  <RInput
                    class="mb-3"
                    iconName="cil-lock-locked"
                    name="Confirm Password"
                    auto="confirm password"
                    icon={true}
                  />

                  <RButton name="Create Account" color="success" block={true} />
                </CForm>
              </CCardBody>
              <CCardFooter className="p-4">
                <CRow>
                  <CCol xs="12" sm="6">
                    <CButton className="btn-facebook mb-1" block>
                      <span>facebook</span>
                    </CButton>
                  </CCol>
                  <CCol xs="12" sm="6">
                    <CButton className="btn-twitter mb-1" block>
                      <span>twitter</span>
                    </CButton>
                  </CCol>
                </CRow>
              </CCardFooter>
            </CCard>
          </CCol>
        </CRow>
      </CContainer>
    </div>
  );
};

export default Register;
