import { createSlice } from "@reduxjs/toolkit";
import API from "src/axios/axios";
import { registerStoreAction } from "./store";

const initialState = {
  isLoading: false,
  message: {
    msg: [],
    color: "",
    status: false,
    isRegistered: false,
  },
};

const registerSlice = createSlice({
  name: "register",
  initialState,
  reducers: {
    sendRequest(state) {
      state.isLoading = true;
    },
    getResponse(state, action) {
      state.isLoading = false,
      state,message
    },
  },
});
export default registerSlice;

export const registerData = (data) => {
  return (dispatch) => {
    dispatch(registerStoreAction.sendRequest());
    console.log(data);
    API.post("users/create", data)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        let array = [];
        for (let key in error.response.data.error) {
          array.push(error.response.data.error[key][0]);
        }
        dispatch(
          registerStoreAction.getResponse({
            data: array,
            status: false,
            color: "danger",
          })
        );
      });
  };
};
