import React, { useEffect } from "react";
import { Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { FormInput } from "src/Components/UI/Input/FormInput";
import useFormValidate from "src/Hooks/input-validation";
import { getModalData } from "src/store/product-slice";
const ModalContentBody = () => {
  const [
    inputValue: productName,
    setInputValue: setName,
    inputChangeHandler: onNameChangeHandler,
    inputBlurHandler: nameBlurHandler,
    reset: nameResetHandler,
  ] = useFormValidate("");
  const mapStateToProps = (state) => {
    return {
      id: state.productStore.modalData.productId,
      isLoading: state.productStore.isLoading,
      modalActionData: state.productStore.modalActionData,
    };
  };
  const state = useSelector(mapStateToProps);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getModalData(state.id));
  }, [dispatch]);

  const onSubmitHandler = (event) => {
    event.preventDefault();
    // const data
  };
  if (!state.isLoading) {
    setName(state.modalActionData.itemname);
  }
  return (
    <>
      {state.isLoading && "loading"}
      {!state.isLoading && (
        <Form onSubmit={onSubmitHandler}>
          <FormInput
            type="text"
            readOnly={true}
            value={productName}
            change={onNameChangeHandler}
            label="Product Name"
          />
          <FormInput
            type="text"
            readOnly={true}
            value={state.modalActionData.itemcode}
            label="Product Code"
          />
          <FormInput
            type="number"
            readOnly={false}
            value={state.modalActionData.count}
            label="Current Stock"
          />
        </Form>
      )}
    </>
  );
};
export default ModalContentBody;
