import { createSlice } from "@reduxjs/toolkit";
import API from "src/axios/axios";
import { userStoreAction } from "./store";

const initialState = {
  isUserDataLoaded: false,
  userData: [],
  reRunComponent: true,
  userModalData: {
    userRoles: ["Admin", "User"],
    userId: 0,
    actionType: "",
    isModalOpen: false,
    modalHeading: "",
    modalAction: "",
    variant: "",
  },
  updateUserData: {
    isLoading: false,
    isDataUpdated: false,
    msg: "",
    color: "",
  },
};

const userSlice = createSlice({
  name: "users",
  initialState,
  reducers: {
    getData(state, action) {
      state.isUserDataLoaded = true;
      state.userData = action.payload.user;
    },
    openUserModal(state, action) {
      state.userModalData = {
        ...state.userModalData,
        isModalOpen: true,
        userId: action.payload.id,
        actionType: action.payload.actionType,
        modalHeading: action.payload.heading,
        variant: action.payload.variant,
        modalAction: action.payload.modalAction,
      };
      console.log(state.userModalData);
    },
    updatingData(state) {
      state.updateUserData = {
        ...state.updateUserData,
        isLoading: true,
      };
    },
    dataUpdated(state , action){
      state.updateUserData = {
        isLoading:false,
        isDataUpdated:true,
      }
    }
  },
});
export default userSlice;

export const getUserData = (param) => {
  return (dispatch) => {
    let url = "users/get-users";
    if (param) {
      url = `users/get-users?${param}`;
    }
    API.get(url)
      .then((response) => {
        if (response.data.http_status === 200) {
          dispatch(userStoreAction.getData({ user: response.data.data }));
        }
      })
      .catch((error) => {
        console.log(error.message);
      });
  };
};

export const getUserModalData = (data) => {
  return (dispatch) => {
    dispatch(
      userStoreAction.openUserModal({
        id: data.id,
        actionType: data.action,
        modalAction: data.action === "assign" ? "Assign Role" : "Disable",
        heading:
          data.action === "assign"
            ? "Assign Role to the User"
            : "Enable/Disable the User",
        variant: data.action === "assign" ? "primary" : "danger",
      })
    );
  };
};

export const editUserRole = (data) => {
  return (dispatch) => {
    dispatch(userStoreAction.updatingData());
    const result = {
      msg: "User Role has been Updated",
    };

  };
};
