import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    sidebarShow: 'responsive'
}

const responsiveSlice = createSlice({
    name:'responsives',
    initialState,
    reducers:{
        changeState(state , action){
            console.log('hi');
             state.sidebarShow = action.payload.sidebarShow;
        }
    },
});

export default responsiveSlice;
