import { createSlice } from "@reduxjs/toolkit";
import { getProductApi } from "src/service/appService";
import { itemStoreAction } from "./store";

const initialState = {
  products: [],
  isPageLoading: false,
  isButtonLoading: false,
  itemCode: [],
  row: 10,
};

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers: {
    sendRequest(state) {
      state.isPageLoading = true;
    },
  },
});
export default itemSlice;

export const getItemData = (row) => {
  return (dispatch) => {
    dispatch(itemStoreAction.sendRequest());
    getProductApi(row).then().catch(error => {
        
    })
  };
};
