import { useState, useEffect } from "react";
import { CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import { useHistory } from "react-router";
import FormInput from "../UI/FormInput";
const EditProduct = () => {
  const history = useHistory();
  const onGoBackHandler = () => {
    history.push("/admin/store/item");
  };
  const [change, setChange] = useState({
    color:['black'],
  })
  return (
    <Form>
      <CRow>
        <CCol md={6} sm={12}>
          <CRow>
            <CCol md={8} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
              />
            </CCol>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Offer Price($)"
                id="offerPrice"
                type="text"
                placeHolder="$0.00"
                readOnly={false}
              />
            </CCol>
          </CRow>
          <CRow>
            <FormInput
              md={12}
              sm={12}
              label="Product Image"
              id="image"
              type="file"
              readOnly={false}
            />
          </CRow>
        </CCol>
        <CCol md={6} sm={12}></CCol>
      </CRow>
    </Form>
  );
};
export default EditProduct;
