import React from "react";
import { Link } from "react-router-dom";
import {
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow,
} from "@coreui/react";
import CIcon from "@coreui/icons-react";
import CAlert from "src/Components/UI/Alert/CAlert";
import useFormValidate from "src/Hooks/input-validation";
import { useSelector } from "react-redux";
import RInput from "src/Components/UI/Register/RInput";
import CButton from "src/Components/UI/Button/Button";

const Login = () => {
  const {
    inputValue: username,
    inputIsTouched: usernameIsTouched,
    inputChangeHandler: usernameChangeHandler,
    inputBlurHandler: usernameBlurHandler,
    reset: usernameResetHandler,
  } = useFormValidate();
  const {
    inputValue: password,
    inputIsTouched: passwordIsTouched,
    inputChangeHandler: passwordChangeHandler,
    inputBlurHandler: passwordBlurHandler,
    reset: passwordResetHandler,
  } = useFormValidate();

  const mapStateToProps = (state) => {
    return {
      isLoading: state.loginStore.isLoading,
      message: state.loginStore.message,
    };
  };
  const state = useSelector(mapStateToProps);
  return (
    <>
      <div
        className="d-flex justify-content-center"
        style={{ marginTop: "10%" }}
      >
        <CContainer className="w-25">
          {state.message.isLoggedIn}
          <CAlert
            color={"danger"}
            text={"Athentication Failed: Incorrect Login Details"}
          />
        </CContainer>
      </div>
      <div
        className="c-app c-default-layout flex-row align-items-center"
        style={{ marginTop: "-10%" }}
      >
        <CContainer>
          <CRow className="justify-content-center">
            <CCol md="8">
              <CCardGroup>
                <CCard className="p-4">
                  <CCardBody>
                    <CForm>
                      <h1>Login</h1>
                      <p className="text-muted">Sign In to your account</p>
                      <RInput
                        class="mb-3"
                        iconName="cil-user"
                        name="User name"
                        auto="username"
                        icon={true}
                        value={username}
                        change={usernameChangeHandler}
                        blur={usernameBlurHandler}
                      />
                      <RInput
                        class="mb-3"
                        iconName="cil-lock-locked"
                        name="Password"
                        auto="password"
                        icon={true}
                        value={password}
                        change={passwordChangeHandler}
                        blur={passwordBlurHandler}
                      />

                      <CRow>
                        <CCol xs="6">
                          <CButton
                            color="primary"
                            type="submit"
                            width="30%"
                            block={false}
                            name="Login"
                            loading={state.isLoading}
                          />
                        </CCol>
                        <CCol xs="6" className="text-right">
                          <CButton
                            color="link"
                            type="button"
                            width="40%"
                            block={false}
                            name=" Forgot password?"
                            loading={false}
                          />
                        </CCol>
                      </CRow>
                    </CForm>
                  </CCardBody>
                </CCard>
                <CCard
                  className="text-white bg-primary py-5 d-md-down-none"
                  style={{ width: "44%" }}
                >
                  <CCardBody className="text-center">
                    <div>
                      <h2>Sign up</h2>
                      <p>
                        SignUp feature on this Portal will be available soon!!
                      </p>
                      <Link to="/home/register">
                        <CButton
                          color="primary"
                          className="mt-3"
                          active
                          tabIndex={-1}
                        >
                          Register Now!
                        </CButton>
                      </Link>
                    </div>
                  </CCardBody>
                </CCard>
              </CCardGroup>
            </CCol>
          </CRow>
        </CContainer>
      </div>
    </>
  );
};

export default Login;
