import { createSlice } from "@reduxjs/toolkit";
import { getProductApi } from "src/service/appService";
import { itemStoreAction } from "./store";

const initialState = {
  products: [],
  isPageLoading: false,
  isButtonLoading: false,
  isDataChanged: true,
  itemCode: [],
  row: 10,
};

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers: {
    sendRequest(state) {
      state.isDataChanged = false;
    },
    dataIsChanged(state) {
      state.isDataChanged = true;
    },
    getResponse(state, action) {
      state.products = action.payload.items;
      state.isPageLoading = true;
      //   console.log(state.products, state.isPageLoading);
    },
  },
});
export default itemSlice;

export const getItemData = (url, row) => {
  return (dispatch) => {
    dispatch(itemStoreAction.sendRequest());
    getProductApi(url, row)
      .then((response) => {
        dispatch(
          itemStoreAction.getResponse({
            items: response.data.data,
          })
        );
      })
      .catch((error) => {
        console.log(error.response);
      });
  };
};
