import { CRow, CCol } from "@coreui/react";
import classes from "./OrderDetails.module.css";
const OrderDetails = (props) => {
  const data = props.data;
  return (
    <>
      {data.map((item, index) => {
        return (
          //   <div className="d-flex justify-content-between" key={index}>
          //     <div className="d-flex justify-content-start">
          //       <div
          //         style={{ width: "25%", height: "15vh" }}
          //         className="mx-auto my-auto"
          //       >
          //         <img src={item.image_url} style={{ width: "100%" }} />
          //       </div>
          //       <div style={{ width: "50%", height: "15vh" }}>
          //         {item.itemname}
          //       </div>
          //     </div>
          //     <div className="d-flex justify-content-start">
          //       <div
          //         style={{ width: "100%", height: "15vh" }}
          //       >{`${item.unitPrice} X ${item.count}`}</div>
          //       <div style={{ width: "100%", height: "15vh" }}>
          //         {item.totalPrice}
          //       </div>
          //     </div>
          //   </div>
          <CRow className="mb-1" key={index}>
            <CCol sm={12} md={3} lg={3} className={`d-flex ${classes.colSize}`}>
              <img
                src={item.image_url}
                className={`mx-auto my-auto ${classes.cartImg}`}
              />
            </CCol>
            <CCol sm={12} md={3} lg={3} className={`d-flex ${classes.colSize}`}>
              <span
                className="mx-auto my-auto font-italic font-weight-bolder"
                style={{ fontSize: "130%" }}
              >
                {item.itemname}
              </span>
            </CCol>
            <CCol sm={12} md={1} lg={1} className={`d-flex ${classes.colSize}`}>
              <span
                className="mx-auto my-auto font-italic font-weight-bolder"
                style={{ fontSize: "130%" }}
              >
                { `item.count`}
              </span>
            </CCol>
          </CRow>
        );
      })}
    </>
  );
};
export default OrderDetails;
