import { useEffect, useState } from "react";
import Card from "src/Components/UI/Card/Card";
import { CRow, CCol } from "@coreui/react";
import { sendGetAdminApi } from "src/service/appService";
import PersonalInfo from "./PersonalInfo";
import ContactInfo from "./ContactInfo";
import PasswordReset from "./PasswordReset";
import Job from "./Job";
const UserDetail = () => {
  const [data, setData] = useState({
    data: "",
    changed: true,
  });
  useEffect(async () => {
    if (data.changed) {
      try {
        const response = await sendGetAdminApi("users/get-a-user");
        setData((prevState) => {
          return {
            ...prevState,
            data: response.data.data,
          };
        });
      } catch (error) {
        console.log(error.response);
      }
      // sendGetAdminApi("users/get-a-user")
      //   .then((response) => {
      //     setData((prevState) => {
      //       return {
      //         ...prevState,
      //         data: response.data.data,
      //       };
      //     });
      //   })
      //   .catch();
    }
  }, [data.changed]);
  // console.log(data.data.job_title);
  // console.log(data);
  return (
    <>
      <CRow>
        <CCol md={6} sm={12}>
          <PersonalInfo body={data.data} show={true} />
          <PasswordReset body={data.data} />
        </CCol>
        <CCol md={6} sm={12}>
          <ContactInfo body={data.data} show={true} />
          <Job body={data.data} />
        </CCol>
      </CRow>
    </>
  );
};
export default UserDetail;
