import { Row } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import Card from "src/Components/UI/Card/Card";
import Icon from "src/Components/UI/Icon/Icon";
const SinglePost = () => {
  const mapStateToProps = (state) => {
    return {
      data: state.postStore.singlePostData,
    };
  };
  const state = useSelector(mapStateToProps);
  const post = state.data.data;
  console.log(state.data.type);
  return (
    <>
      <Row>
        <Col sm={12} className="text-right mb-3">
          <CButton
            name="Add Post"
            color="success"
            click={openCreateModalHandler}
            // loading={state.addSaleModalData.isLoading}
          />
        </Col>
      </Row>
      <Card
        color="primary"
        header={post.title}
        subTitle={true}
        by={post.name}
        date={post.created_at}
      >
        <div className="mx-3 my-2">
          <div className="text-justify mb-3">{post.content}</div>
          <div>Likes: {post.likes_count}</div>
          <div>comments: {post.comments_count}</div>
        </div>
      </Card>
    </>
  );
};
export default SinglePost;
