import React, { useEffect } from "react";
import { Form } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import API from "src/axios/axios";
import { FormInput } from "src/Components/UI/Input/FormInput";
import useFormValidate from "src/Hooks/input-validation";
import { getModalData } from "src/store/product-slice";
import { productStoreAction } from "src/store/store";
const ModalContentBody = () => {
  const { inputValue: productName, setInputValue: setName } =
    useFormValidate("");
  const { inputValue: productCode, setInputValue: setCode } =
    useFormValidate("");
  const {
    inputValue: stock,
    setInputValue: setStock,
    inputChangeHandler: onStockChangehandler,
  } = useFormValidate("");
  const mapStateToProps = (state) => {
    return {
      id: state.productStore.modalData.productId,
      isLoading: state.productStore.isLoading,
    };
  };
  const state = useSelector(mapStateToProps);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(productStoreAction.isDataLoading());
    API.get(`get-product-details/${state.id}`)
      .then((response) => {
        dispatch(productStoreAction.dataIsLoaded());
        if (response.data.http_status === 200) {
          setName(response.data.data[0].itemname);
          setCode(response.data.data[0].itemcode);
          setStock(response.data.data[0].count);
        }
      })
      .catch((error) => {
        console.log(error);
      });
  }, [dispatch]);

  const onSubmitHandler = (event) => {
    event.preventDefault();
    const data = {
      itemId: state.id,
      itemCount: +stock,
      action: "update",
    };
    console.log(data);
  };

  return (
    <>
      {state.isLoading && "loading"}
      {!state.isLoading && (
        <Form onSubmit={onSubmitHandler}>
          <FormInput
            type="text"
            readOnly={true}
            value={productName}
            label="Product Name"
          />
          <FormInput
            type="text"
            readOnly={true}
            value={productCode}
            label="Product Code"
          />
          <FormInput
            type="number"
            readOnly={false}
            value={stock}
            change={onStockChangehandler}
            label="Current Stock"
          />
        </Form>
      )}
    </>
  );
};
export default ModalContentBody;
