import { createSlice } from "@reduxjs/toolkit";
import { getUrl } from "src/service/customService";

const initialState = {
  tableData: {
    data: [],
    isDataLoaded: false,
  },
  reRunComponent: {
    isDataChanged: true,
    queryParam: "",
  },
};

const commentSlice = createSlice({
  name: "comments",
  initialState,
  reducers: {},
});
export default commentSlice;

export const getCommentData = (data) => {
  return (dispatch) => {
    const url = getUrl(`comments/get-comments`, data.param);
    // sendGetAdminApi(url);
  };
};
