import { useState } from "react";
import { CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import classes from "./Store.module.css";
import SFormSelect from "./UI/FormSelect";
import FormInput from "./UI/FormInput";
import { required } from "./UI/validator/Validator";
import { sendPostAdminApi, sendPostApi } from "src/service/appService";
import SAlert from "./UI/Alert/Alert";
const Store = () => {
  const [alert, setAlert] = useState({
    msg: "",
    status: "",
    show: false,
  });
  const [formData, setFormData] = useState({
    name: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    price: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    color: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    quantity: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    status: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    image: {
      value: "draft",
      valid: false,
      validator: [required],
      src: "",
    },
    isFormValid: false,
  });

  const onInputChangeHandler = (event, input) => {
    let value;
    let url = "";
    let data;
    if (input === "price") {
      value = Number.parseFloat(event.target.value);
    } else if (input === "image") {
      url = URL.createObjectURL(event.target.files[0]);
      value = event.target.files[0];
    } else {
      value = event.target.value;
    }
    setFormData((prevState) => {
      let isValid = true;
      const updatedForm = {
        ...prevState,
        [input]: {
          ...prevState[input],
          value: value,
          valid: isValid,
          src: url,
        },
      };
      return updatedForm;
    });
  };
  const onSubmitHandler = async (e) => {
    e.preventDefault();
    var product = new FormData();
    product.append("name", formData.name.value);
    product.append("price", formData.price.value);
    product.append("file", formData.image.value);
    product.append("color", formData.color.value);
    product.append("status", formData.status.value);
    product.append("quantity", formData.quantity.value);
    try {
      const response = await sendPostAdminApi("store/add-product", product);
      setAlert((prevState) => {
        return {
          ...prevState,
          msg: response.data.data.msg,
          status: "success",
          show: true,
        };
      });
      setFormData((prevState) => {
        const array = ["name"];
        let updateForm;
        array.forEach((element, index) => {
          const updatedForm = {
            ...prevState,
            [element]: {
              value: "",
              valid: false,
              validator: [required],
              src: "",
            },
          };
        });

        return updatedForm;
      });
    } catch (error) {
      setAlert((prevState) => {
        return {
          ...prevState,
          msg: error.response.data.error,
          status: "danger",
          show: true,
        };
      });
    }
  };
  const closeAlertHandler = () => {
    setAlert((prevState) => {
      return {
        ...prevState,
        msg: "",
        status: "",
        show: false,
      };
    });
  };
  return (
    <>
      {alert.show && (
        <SAlert
          color={alert.status}
          msg={alert.msg}
          close={closeAlertHandler}
        />
      )}
      <Form onSubmit={onSubmitHandler}>
        <CRow>
          <CCol md={6} sm={12}>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
                value={formData.name.value}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
                placeHolder="$0.00"
                value={formData.price.value}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Image"
                id="image"
                type="file"
                readOnly={false}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={6}
                sm={12}
                label="Color"
                id="color"
                type="text"
                readOnly={false}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow className="ml-1">
              <SFormSelect
                md={12}
                sm={12}
                label="Product Status"
                id="status"
                options={["draft", "active"]}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Quantity"
                type="number"
                readOnly={false}
                id="quantity"
                placeHolder="1"
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow className="mx-auto">
              <CButton
                color="success"
                width="30%"
                type="submit"
                name="Save Product"
                block={false}
                width="30%"
                // disabled={input.isFormValid ? false : true}
              />
            </CRow>
          </CCol>
          <CCol md={5} sm={12} className="d-flex">
            <CCol
              xs={4}
              sm={3}
              md={4}
              lg={4}
              xl={4}
              className="mx-auto my-auto"
            >
              {formData.image.src && (
                <img
                  src={formData.image.src}
                  className={classes.productImage}
                />
              )}
            </CCol>
          </CCol>
        </CRow>
      </Form>
    </>
  );
};
export default Store;
