import { useDispatch } from "react-redux";
import { getDate } from "src/custom-functions";
import Icon from "src/Components/UI/Icon/Icon";
import classes from "./Product.module.css";
import ToolTip from "src/Components/UI/Tooltip/ToolTip";
import { productStoreAction } from "src/store/store";
import { getModalData } from "src/store/product-slice";
const CTableBody = (props) => {
  const dispatch = useDispatch();
  const viewHandler = (id) => {
    const data = {
      id,
      action: "Update",
    };
    dispatch(getModalData(data));
  };
  const deleteHandler = (id) => {
    const data = {
      id,
      action: "Delete",
      heading: "Delete the Product",
      variant: "danger",
    };
    dispatch(productStoreAction.modalOpen(data));
  };
  return (
    <>
      {props.body.map((row, i) => {
        return (
          <tr key={i}>
            <td>{row.id}</td>
            <td>{row.itemname}</td>
            <td>{row.itemcode}</td>
            <td>{row.count}</td>
            <td>{getDate(row.updated_at)}</td>
            <td>
              <ToolTip value="Update">
                <Icon
                  class={classes.view}
                  name="cil-align-center"
                  action={viewHandler.bind(null, row.id)}
                />
              </ToolTip>
              |
              <ToolTip value="Delete">
                <Icon
                  class={classes.delete}
                  name="cil-trash"
                  action={deleteHandler.bind(null, row.id)}
                />
              </ToolTip>
            </td>
          </tr>
        );
      })}
    </>
  );
};
export default CTableBody;
