export const required = (value) => value.trim() !== "";

export const email = (value) =>
  /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/.test(
    value
  );
export const phone = (value) => /^[+][1-9][0-9][-][0-9]{6,14}$/.test(value);

export const length = (config) => (value) => {
  let isValid = true;
  console.log(config, value);
  if (config.min) {
    isValid = isValid && value.trim().length >= value.min;
  }
  if (config.max) {
    isValid = isValid && value.trim().length <= value.min;
  }
  return isValid;
};

export const validPassword = (password) => (value) =>
  password.trim() === value.trim();
