import { useState } from "react";
import { CButtonToolbar, CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import classes from "./Store.module.css";
import SFormSelect from "./UI/FormSelect";
import FormInput from "./UI/FormInput";
import { required } from "./UI/validator/Validator";
import { sendPostAdminApi, sendPostApi } from "src/service/appService";
import SAlert from "./UI/Alert/Alert";
import SButtonGroup from "./UI/SButtonGroup/SButtonGroup";
const Store = () => {
  const [alert, setAlert] = useState({
    msg: "",
    status: "",
    show: false,
  });
  const [change, setChange] = useState({ color: ["black"], height: ["44cm"] });
  const [formData, setFormData] = useState({
    name: {
      value: "",
      valid: false,
    },
    price: {
      value: "",
      valid: false,
    },
    color: {
      value: "black",
      valid: true,
    },
    height: { value: "44cm", valid: true },
    status: { value: "active", valid: true },
  });
  let isFormValid = true;

  const onInputChangeHandler = (event, input) => {
    let value = event.target.value;
    if (input === "color" || input === "height") {
      let array = change[input].slice();
      if (!array.includes(value)) {
        array.push(value);
        setChange((prevState) => {
          return {
            ...prevState,
            [input]: array,
          };
        });
      }
    }
    setFormData((prevState) => {
      let isValid = true;
      const updatedForm = {
        ...prevState,
        [input]: {
          ...prevState[input],
          value: value,
          valid: isValid,
        },
      };
      return updatedForm;
    });
  };
  //check if it is valid
  for (const input in formData) {
    isFormValid = isFormValid && formData[input].valid;
  }
  // const onInputChangeHandler = (event, input) => {
  //   let value;
  //   let url = "";
  //   let data;
  //   if (input === "price") {
  //     value = Number.parseFloat(event.target.value);
  //   } else if (input === "image") {
  //     url = URL.createObjectURL(event.target.files[0]);
  //     value = event.target.files[0];
  //   } else {
  //     value = event.target.value;
  //   }
  //   setFormData((prevState) => {
  //     let isValid = true;
  //     const updatedForm = {
  //       ...prevState,
  //       [input]: {
  //         ...prevState[input],
  //         value: value,
  //         valid: isValid,
  //         src: url,
  //       },
  //     };
  //     return updatedForm;
  //   });
  // };
  const onSubmitHandler = async (e) => {
    e.preventDefault();
    // var product = new FormData();
    // product.append("name", formData.name.value);
    // product.append("price", formData.price.value);
    // product.append("file", formData.image.value);
    // product.append("color", formData.color.value);
    // product.append("status", formData.status.value);
    // product.append("quantity", formData.quantity.value);
    // try {
    //   const response = await sendPostAdminApi("store/add-product", product);
    //   setAlert((prevState) => {
    //     return {
    //       ...prevState,
    //       msg: response.data.data.msg,
    //       status: "success",
    //       show: true,
    //     };
    //   });
    //   setFormData((prevState) => {
    //     const array = ["name", "price"];
    //     let updateForm;
    //     array.forEach((element, index) => {
    //       updateForm = {
    //         ...prevState,
    //         [element]: {
    //           ...prevState[element],
    //           value: "",
    //           valid: false,
    //           src: "",
    //         },
    //       };
    //     });

    //     return updateForm;
    //   });
    // } catch (error) {
    //   setAlert((prevState) => {
    //     return {
    //       ...prevState,
    //       msg: error.response.data.error,
    //       status: "danger",
    //       show: true,
    //     };
    //   });
    // }
  };
  const closeAlertHandler = () => {
    setAlert((prevState) => {
      return {
        ...prevState,
        msg: "",
        status: "",
        show: false,
      };
    });
  };

  const onArrayChangeHandler = (type, index) => {
    let array = change[type].slice();
    array.splice(index, 1);
    setChange((prevState) => {
      return {
        ...prevState,
        [type]: array,
      };
    });
  };
  return (
    <>
      {alert.show && (
        <SAlert
          color={alert.status}
          msg={alert.msg}
          close={closeAlertHandler}
        />
      )}

      <Form onSubmit={onSubmitHandler}>
        <CRow>
          <CCol md={6} sm={12}>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
                value={formData.name.value}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
                placeHolder="$0.00"
                value={formData.price.value}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Image"
                id="image"
                type="file"
                readOnly={false}
                // change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <CCol>
                <div className="mb-2">Product Color</div>
                {change.color.length > 0 && (
                  <CButtonToolbar>
                    {change.color.map((el, i) => {
                      return (
                        <div className="mb-2" key={i}>
                          <SButtonGroup
                            color={el}
                            control={true}
                            input="color"
                            index={i}
                            click={onArrayChangeHandler}
                          />
                        </div>
                      );
                    })}
                  </CButtonToolbar>
                )}

                <div style={{ display: "inline-block" }}>
                  <SFormSelect
                    md={4}
                    sm={12}
                    label={false}
                    id="color"
                    options={["black", "white", "walnet", "Natural"]}
                    change={onInputChangeHandler}
                  />
                </div>
              </CCol>
            </CRow>
            <CRow>
              <CCol>
                <div className="mb-2">Product Height</div>
                {change.height.length > 0 && (
                  <CButtonToolbar>
                    {change.height.map((el, i) => {
                      return (
                        <div className="mb-2" key={i}>
                          <SButtonGroup
                            color={el}
                            control={true}
                            input="height"
                            index={i}
                            click={onArrayChangeHandler}
                          />
                        </div>
                      );
                    })}
                  </CButtonToolbar>
                )}
                <div style={{ display: "inline-block" }}>
                  <SFormSelect
                    md={12}
                    sm={12}
                    label={false}
                    id="height"
                    options={["44cm", "68em", "74cm"]}
                    change={onInputChangeHandler}
                  />
                </div>
              </CCol>
            </CRow>
            <CRow className="ml-1">
              <SFormSelect
                md={12}
                sm={12}
                label="Product Status"
                id="status"
                options={["draft", "active"]}
                value={formData.status.value}
                change={onInputChangeHandler}
              />
            </CRow>

            <CRow className="mx-auto">
              <CButton
                color="success"
                width="30%"
                type="submit"
                name="Save Product"
                block={false}
                width="30%"
                disabled={isFormValid ? false : true}
              />
            </CRow>
          </CCol>
          <CCol md={5} sm={12} className="d-flex">
            <CCol
              xs={4}
              sm={3}
              md={4}
              lg={4}
              xl={4}
              className="mx-auto my-auto"
            >
              {/* {formData.image.src && (
                <img
                  src={formData.image.src}
                  className={classes.productImage}
                />
              )} */}
            </CCol>
          </CCol>
        </CRow>
      </Form>
    </>
  );
};
export default Store;
