import { useState } from "react";
import { CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import FormInputLabel from "src/Components/UI/Input/FormInputLabel";
import FormSelect from "src/Components/UI/Input/FormSelect";
import classes from "./Store.module.css";
const Store = () => {
  const [isFormValid, set] = useState(initialState)
  const [image, setImage] = useState({
    image: "",
    src: "",
  });
  const fileChangeHandler = (event) => {
    const url = URL.createObjectURL(event.target.files[0]);
    setImage((prevState) => {
      return {
        ...prevState,
        image: event.target.files[0],
        src: url,
      };
    });
  };
  return (
    <>
      <Form>
        <CRow>
          <CCol md={6} sm={12}>
            <CRow>
              <FormInputLabel
                md={12}
                sm={12}
                label="Product Name"
                type="text"
                readOnly={false}
              />
            </CRow>
            <CRow>
              <FormInputLabel
                md={12}
                sm={12}
                label="Price"
                type="text"
                readOnly={false}
                placeHolder="$ 0.00"
              />
            </CRow>
            <CRow>
              <FormInputLabel
                md={12}
                sm={12}
                label="Product Image"
                type="file"
                readOnly={false}
                change={fileChangeHandler}
              />
            </CRow>
            <CRow className="ml-1">
              <FormSelect
                md={12}
                sm={12}
                label="Product Status"
                options={["draft", "active"]}
              />
            </CRow>
            <CRow>
              <FormInputLabel
                md={12}
                sm={12}
                label="Quantity"
                type="number"
                readOnly={false}
                placeHolder="1"
              />
            </CRow>
            <CRow className="mx-auto">
              <CButton
                color="success"
                width="30%"
                type="submit"
                name="Save Product"
                block={false}
                width="30%"
                disabled={isFormValid ? true : false}
              />
            </CRow>
          </CCol>
          <CCol md={5} sm={12} className="d-flex">
            <CCol
              xs={4}
              sm={3}
              md={4}
              lg={4}
              xl={4}
              className="mx-auto my-auto"
            >
              {image.src && (
                <img src={image.src} className={classes.productImage} />
              )}
            </CCol>
          </CCol>
        </CRow>
      </Form>
    </>
  );
};
export default Store;
