import { createSlice } from "@reduxjs/toolkit";
import { sendPostApi } from "src/service/appService";
import { loginStoreAction } from "./store";

const initialState = {
  isLoading: false,
  loggedInData: {
    userName: "",
    name: "",
    token: null,
    role_id: 0,
  },
  message: {
    msg: "",
    color: "",
    isLoggedIn: false,
  },
};

const loginSlice = createSlice({
  name: "login",
  initialState,
  reducers: {
    sendLoginRequest(state) {
      state.isLoading = true;
      state.message = {
        ...state.message,
        msg: "Authenticating......",
        color: "success",
        isLoggedIn: true,
      };
    },
  },
});
export default loginSlice;

export const LoginRequest = (data, history) => {
  return (dispatch) => {
    console.log(data, history);
    dispatch(loginStoreAction.sendLoginRequest());
    sendPostApi(data)
  };
};
