import { useState } from "react";
import { CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import FormInputLabel from "src/Components/UI/Input/FormInputLabel";
import FormSelect from "src/Components/UI/Input/FormSelect";
import classes from "./Store.module.css";
const Store = () => {
  const [image, setImage] = useState({
    image: "",
    src: "",
  });
  const fileChangeHandler = (e) => {
    const url = URL.createObjectURL(event.target.files[0]);
    setImage((prevState) => {
      return {
        ...prevState,
        image: event.target.files[0],
        src: url,
      };
    });
  };
  return (
    <>
      <Form>
        <CRow>
          <FormInputLabel
            md={6}
            sm={12}
            label="Product Name"
            type="text"
            readOnly={false}
          />
        </CRow>
        <CRow>
          <FormInputLabel
            md={6}
            sm={12}
            label="Price"
            type="text"
            readOnly={false}
            placeHolder="$ 0.00"
          />
        </CRow>
        <CRow>
          <FormInputLabel
            md={6}
            sm={12}
            label="Product Image"
            type="file"
            readOnly={false}
            change={fileChangeHandler}
          />
          <C xs={4} sm={3} md={4} lg={4} xl={4}>
            <img src={image.src} className={classes.productImage} />
          </C>
        </CRow>
        <CRow className="ml-1">
          <FormSelect
            md={6}
            sm={12}
            label="Product Status"
            options={["draft", "active"]}
          />
        </CRow>
        <CRow>
          <FormInputLabel
            md={6}
            sm={12}
            label="Quantity"
            type="number"
            readOnly={false}
            placeHolder="1"
          />
        </CRow>
        <CRow className="ml-1">
          <CButton
            color="success"
            width="30%"
            type="submit"
            name="Save Product"
            block={false}
            width="30%"
          />
        </CRow>
      </Form>
    </>
  );
};
export default Store;
