import { createSlice } from "@reduxjs/toolkit";
import { getUrl } from "src/service/customService";

const initialState = {
  tableData: {
    data: [],
    isDataLoaded: false,
  },
  reRunComponent: {
    isDataChanged: true,
    queryParam: "",
  },
};

const commentSlice = createSlice({
  name: "comments",
  initialState,
  reducers: {},
});
export default commentSlice;

export const getCommentData = (data) => {
  return (dispatch) => {
    const url = getUrl(`comments/get-comments`, data.param);
    console.log(url);
    sendGetAdminApi(url)
      .then((response) => {
          console.log();
        // if (response.data.http_status === 200) {
        //   dispatch(postStoreAction.getData({ post: response.data.data }));
        // }
      })
      .catch((error) => {
        console.log(error.message);
      });
  };
};
