import { createSlice } from "@reduxjs/toolkit";
import { sendPostApi } from "src/service/appService";
import { loginStoreAction } from "./store";

const initialState = {
  isLoading: false,
  loggedInData: {
    userName: "",
    name: "",
    token: null,
    role_id: 0,
  },
  message: {
    msg: "",
    color: "",
    isLoggedIn: false,
  },
  admin: false,
};

const loginSlice = createSlice({
  name: "login",
  initialState,
  reducers: {
    sendLoginRequest(state) {
      state.isLoading = true;
      state.message = {
        ...state.message,
        msg: "Authenticating......",
        color: "success",
        isLoggedIn: true,
      };
    },

    getLoginErrorResponse(state, action) {
      state.isLoading = false;
      state.message = {
        ...state.message,
        msg: action.payload.msg,
        color: action.payload.color,
        isLoggedIn: true,
      };
    },
    getLoginSuccessResponse(state, action) {
      state.isLoading = false;
      state.message = {
        ...state.message,
        msg: action.payload.msg,
        color: action.payload.color,
        isLoggedIn: true,
      };
      state.loggedInData = {
        ...state.loggedInData,
        name: action.payload.name,
      };
    },
    updateToken(state, action) {
      state.loggedInData = {
        ...state.loggedInData,
        token: action.payload.token,
      };
      console.log(state.loggedInData);
    },
  },
});
export default loginSlice;

export const LoginRequest = (data, history) => {
  return (dispatch) => {
    dispatch(loginStoreAction.sendLoginRequest());

    sendPostApi("user/login", data)
      .then((response) => {
        localStorage.setItem("token", response.data.data.token);
        dispatch(
          loginStoreAction.getLoginSuccessResponse({
            msg: "Authentication Successfull:Rediracting to Admin Panel!!",
            color: "success",
            name: response.data.data.name,
          })
        );
        setTimeout(() => {
          dispatch(
            loginStoreAction.updateToken({
              token: response.data.data.token,
            })
          );
          history.replace("/admin");
        }, 2000);
      })
      .catch((error) => {
        dispatch(
          loginStoreAction.getLoginErrorResponse({
            msg: error.response.data.error,
            color: "danger",
          })
        );
      });
  };
};
export const logout = () => {
  return (dispatch) => {
    localStorage.removeItem("token");
    dispatch(loginStoreAction.logout());
  };
};
