import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  tableData: {
    data: [],
    isDataLoaded: false,
  },
  reRunComponent: {
    isDataChanged: true,
    queryParam: "",
  },
};

const commentSlice = createSlice({
  name: "comments",
  initialState,
  reducers: {},
});
export default commentSlice;
