import { createSlice } from "@reduxjs/toolkit";
import { sendGetApi } from "src/service/appService";
import { getUrl } from "src/service/customService";
import { shopStoreAction } from "./store";

const initialState = {
  shop: {
    product: [],
    isDataReceived: false,
    reRunComponent: true,
  },
};

const shopSlice = createSlice({
  name: "shop",
  initialState,
  reducers: {
    sendRequest(state) {
      state.shop = {
        ...state.shop,
        isDataReceived: false,
      };
    },
    getStoreProduct(state, action) {
      state.shop = {
        ...state.shop,
        product: action.payload.data,
        param: "",
      };
    },
    getResponse(state) {
      state.shop = {
        ...state.shop,
        isDataReceived: true,
      };
    },
  },
});
export default shopSlice;

export const getStoreProductData = (param) => {
  return async (dispatch) => {
    dispatch(shopStoreAction.sendRequest());
    try {
      const url = getUrl(`store/get-products`, param);
      console.log(url);
      const response = await sendGetApi(url);
      dispatch(
        shopStoreAction.getStoreProduct({
          data: response.data.data,
        })
      );
      dispatch(shopStoreAction.getResponse());
    } catch (error) {
      console.log(error.response);
    }
  };
};
