import { useState } from "react";
import { CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import FormInputLabel from "src/Components/UI/Input/FormInputLabel";
import FormSelect from "src/Components/UI/Input/FormSelect";
import classes from "./Store.module.css";
import SFormSelect from "./UI/FormSelect";
import FormInput from "./UI/FormInput";
import { required } from "./UI/validator/Validator";
import { API_URL } from "src/axios/api";
const Store = () => {
  const [image, setImage] = useState({
    image: "",
    src: "",
  });
  const [formData, setFormData] = useState({
    name: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    price: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    color: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    quantity: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    material: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    status: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    image: {
      value: "",
      valid: false,
      validator: [required],
      src: "",
    },
    isFormValid: false,
  });
  const fileChangeHandler = (event) => {
    const url = URL.createObjectURL(event.target.files[0]);
    setImage((prevState) => {
      return {
        ...prevState,
        image: event.target.files[0],
        src: url,
      };
    });
  };

  const onInputChangeHandler = (event, input) => {
    // console.log(input, event.target.value);
    let value;
    let url = "";
    let data;
    if (input === "price") {
      value = Number.parseFloat(event.target.value);
    } else if (input === "image") {
      url = URL.createObjectURL(event.target.files[0]);
      value = event.target.files[0];
    } else {
      value = event.target.value;
    }
    setFormData((prevState) => {
      let isValid = true;
      const updatedForm = {
        ...prevState,
        [input]: {
          value: value,
          valid: isValid,
          src: url,
        },
      };
      return updatedForm;
    });
    // console.log(formData);
  };
  const onSubmitHandler = async (e) => {
    e.preventDefault();
    // var product = new FormData();
    // product.append("name", formData.name.value);
    // product.append("price", formData.price.value);
    // product.append("file", formData.image.value);
    // product.append("color", formData.color.value);
    // product.append("material", formData.material.value);
    // product.append("status", formData.status.value);
    let product = {
      name: "nshanh",
    };
    try {
      const sendData = await fetch(`${API_URL}store/add-product`, {
        method: "post",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(product),
      });
      const response = await sendData.json();
      console.log(response, "hi");
    } catch (error) {
      console.log(error.message, "hiq");
    }
  };
  return (
    <>
      <Form onSubmit={onSubmitHandler}>
        <CRow>
          <CCol md={6} sm={12}>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
                placeHolder="$0.00"
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Product Image"
                id="image"
                type="file"
                readOnly={false}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={6}
                sm={12}
                label="Color"
                id="color"
                type="text"
                readOnly={false}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow className="ml-1">
              <SFormSelect
                md={12}
                sm={12}
                label="Product Materials"
                id="material"
                options={["wood", "steel"]}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow className="ml-1">
              <SFormSelect
                md={12}
                sm={12}
                label="Product Status"
                id="status"
                options={["draft", "active"]}
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow>
              <FormInput
                md={12}
                sm={12}
                label="Quantity"
                type="number"
                readOnly={false}
                id="quantity"
                placeHolder="1"
                change={onInputChangeHandler}
              />
            </CRow>
            <CRow className="mx-auto">
              <CButton
                color="success"
                width="30%"
                type="submit"
                name="Save Product"
                block={false}
                width="30%"
                // disabled={input.isFormValid ? false : true}
              />
            </CRow>
          </CCol>
          <CCol md={5} sm={12} className="d-flex">
            <CCol
              xs={4}
              sm={3}
              md={4}
              lg={4}
              xl={4}
              className="mx-auto my-auto"
            >
              {formData.image.src && (
                <img
                  src={formData.image.src}
                  className={classes.productImage}
                />
              )}
            </CCol>
          </CCol>
        </CRow>
      </Form>
    </>
  );
};
export default Store;
