import { createSlice } from "@reduxjs/toolkit";
import { sendGetApi } from "src/service/appService";
import { shopStoreAction } from "./store";

const initialState = {
  shop: {
    product: [],
    isDataReceived: false,
    reRunComponent: true,
  },
};

const shopSlice = createSlice({
  name: "shop",
  initialState,
  reducers: {
    sendRequest(state) {
      state.shop = {
        ...state.shop,
        isDataReceived: false,
      };
    },
    getStoreProduct(state, action) {},
    getResponse(state) {
      state.shop = {
        ...state.shop,
        isDataReceived: true,
      };
    },
  },
});
export default shopSlice;

export const getStoreProductData = () => {
  return async (dispatch) => {
    dispatch(shopStoreAction.sendRequest());
    try {
      const response = await sendGetApi(`store/get-products`, "10");
      console.log(response.data.data);
    } catch (error) {
      console.log(error.response);
    }
  };
};
