import { useState, useEffect } from "react";
import { CButtonToolbar, CCol, CRow } from "@coreui/react";
import { useSelector } from "react-redux";
import { Form } from "react-bootstrap";
import { useHistory } from "react-router";
import FormInput from "../UI/FormInput";
import SButtonGroup from "../UI/SButtonGroup/SButtonGroup";
import SFormSelect from "../UI/FormSelect";
import SFormTextArea from "../UI/SFormTextArea";
import CButton from "src/Components/UI/Button/Button";
import { decimal, offer, required } from "../UI/validator/Validator";
import classes from "./Product.module.css";
const EditProduct = () => {
  const history = useHistory();
  const onGoBackHandler = () => {
    history.push("/admin/store/item");
  };
  const [change, setChange] = useState({
    color: ["black"],
    height: ["46cm"],
  });
  const [formData, setFormData] = useState({
    name: {
      value: "",
      valid: false,
      validators: [required],
    },
    price: {
      value: "",
      offerValue: "",
      valid: false,
      validators: [required, decimal],
    },
    offerPrice: {
      value: "",
      valid: true,
      validators: [offer],
    },
    color: {
      value: "black",
      valid: true,
      validators: [required],
    },
    image: {
      value: "",
      file: "",
      valid: false,
      src: "",
      validators: [required],
    },
    description: {
      value: "",
      valid: false,
      validators: [required],
    },
    height: { value: "46cm", valid: true, validators: [required] },
    status: { value: "active", valid: true, validators: [required] },
  });
  let isFormValid = true;
  const mapStateToProps = (state) => {
    return {
      product: state.shopStore.singaleProductData.data,
    };
  };
  const state = useSelector(mapStateToProps);
  useEffect(() => {
    console.log(state.product.color.split(''));
    // setChange((prevState) => {
    //   return {
    //     ...prevState,
    //     color: state.product.color.split(","),
    //   };
    // });
    setFormData((prevState) => {
      return {
        ...prevState,
        name: {
          ...prevState["name"],
          value: state.product.name,
          valid: true,
        },
        price: {
          ...prevState["price"],
          value: state.product.price,
          valid: true,
        },
        offerPrice: {
          ...prevState["offerPrice"],
          value: state.product.offer_price ? state.product.offer_price : "",
          valid: true,
        },
        image: {
          ...prevState["image"],
          src: state.product.image_url,
          valid: true,
        },
      };
    });
  }, [state.product, setFormData, setChange]);

  const onInputChangeHandler = (type, index) => {};
  return (
    <Form>
      <CRow>
        <CCol md={6} sm={12}>
          <CRow>
            <CCol md={8} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
                value={formData.name.value}
                change={onInputChangeHandler}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
                value={formData.price.value}
                change={onInputChangeHandler}
              />
            </CCol>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Offer Price($)"
                id="offerPrice"
                type="text"
                placeHolder="$0.00"
                readOnly={false}
                value={formData.offerPrice.value}
                change={onInputChangeHandler}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol className="mr-1">
              <FormInput
                md={12}
                sm={12}
                label="Product Image"
                id="image"
                type="file"
                readOnly={false}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol className="ml-3">
              <div className="mb-2">Product Color</div>
              {change.color.length > 0 && (
                <CButtonToolbar>
                  {change.color.map((el, i) => {
                    return (
                      <div className="mb-2" key={i}>
                        <SButtonGroup
                          color={el}
                          control={true}
                          input="color"
                          index={i}
                        />
                      </div>
                    );
                  })}
                </CButtonToolbar>
              )}

              <div style={{ display: "inline-block" }}>
                <SFormSelect
                  md={4}
                  sm={12}
                  label={false}
                  id="color"
                  options={["black", "white", "walnet", "Natural"]}
                />
              </div>
            </CCol>
          </CRow>
          <CRow>
            <CCol className="ml-3">
              <div className="mb-2">Product Height</div>
              {change.height.length > 0 && (
                <CButtonToolbar>
                  {change.height.map((el, i) => {
                    return (
                      <div className="mb-2" key={i}>
                        <SButtonGroup
                          color={el}
                          control={true}
                          input="height"
                          index={i}
                        />
                      </div>
                    );
                  })}
                </CButtonToolbar>
              )}
              <div style={{ display: "inline-block" }}>
                <SFormSelect
                  md={12}
                  sm={12}
                  label={false}
                  id="height"
                  options={["46cm", "68cm", "74cm"]}
                />
              </div>
            </CCol>
          </CRow>
          <CRow>
            <CCol>
              <SFormTextArea
                rows="5"
                labelName="Product Description"
                id="description"
                type="textarea"
                readOnly={false}
              ></SFormTextArea>
            </CCol>
          </CRow>
          <CRow className="ml-1">
            <CCol md={4} sm={12}>
              <SFormSelect
                md={12}
                sm={12}
                label="Product Status"
                id="status"
                options={["draft", "active"]}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol className="ml-3">
              <CButton
                color="success"
                width="30%"
                type="submit"
                name="Save Product"
                block={false}
                width="30%"
              />
            </CCol>
          </CRow>
        </CCol>
        <CCol md={6} sm={12} className="d-flex">
          <CCol xs={4} sm={3} md={4} lg={4} xl={4} className="mx-auto my-auto">
            {formData.image.src && (
              <img src={formData.image.src} className={classes.productImage} />
            )}
          </CCol>
        </CCol>
      </CRow>
    </Form>
  );
};
export default EditProduct;
