import { Form } from "react-bootstrap";

const FormTextArea = (props) => {
  return (
    <Form.Group>
      <label htmlFor={props.id}>{props.labelName}</label>
      <textarea
        className="form-control"
        id={props.id}
        readOnly={props.readOnly}
        onChange={props.change}
        value={props.value}
        rows={props.rows}
        value={props.value}
      >
        {props.children}
      </textarea>
    </Form.Group>
  );
};
export default FormTextArea;
