import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import Loader from "src/Components/UI/Loader/Loader";
import { getUserData } from "src/store/user-slice";
const AllUsers = () => {
  const mapStateToProps = (state) => {
    return {
      isDataChanged: state.userStore.isDataChanged,
      userData: state.userStore.userData,
      reRunComponent: state.userStore.reRunComponent,
    };
  };
  const state = useSelector(mapStateToProps);
  const dispatch = useDispatch();
  useEffect(() => {
    if (state.isDataChanged) {
      dispatch(getUserData());
    }
  }, [state.isDataChanged, dispatch]);
  return <>{!state.isDataLoaded && <Loader />}</>;
};
export default AllUsers;
