import { useState, useEffect } from "react";
import { CButtonToolbar, CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import { useHistory } from "react-router";
import FormInput from "../UI/FormInput";
import SButtonGroup from "../UI/SButtonGroup/SButtonGroup";
import SFormSelect from "../UI/FormSelect";
const EditProduct = () => {
  const history = useHistory();
  const onGoBackHandler = () => {
    history.push("/admin/store/item");
  };
  const [change, setChange] = useState({
    color: ["black"],
    height: ["44cm"],
  });
  return (
    <Form>
      <CRow>
        <CCol md={6} sm={12}>
          <CRow>
            <CCol md={8} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Product Name"
                id="name"
                type="text"
                readOnly={false}
              />
            </CCol>
          </CRow>
          <CRow>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Price($)"
                id="price"
                type="text"
                readOnly={false}
              />
            </CCol>
            <CCol md={4} sm={12}>
              <FormInput
                md={12}
                sm={12}
                label="Offer Price($)"
                id="offerPrice"
                type="text"
                placeHolder="$0.00"
                readOnly={false}
              />
            </CCol>
          </CRow>
          <CRow>
            <FormInput
              md={12}
              sm={12}
              label="Product Image"
              id="image"
              type="file"
              readOnly={false}
            />
          </CRow>
          <CRow>
            <CCol>
              <div className="mb-2">Product Color</div>
              {change.color.length > 0 && (
                <CButtonToolbar>
                  {change.color.map((el, i) => {
                    return (
                      <div className="mb-2" key={i}>
                        <SButtonGroup
                          color={el}
                          control={true}
                          input="color"
                          index={i}
                        />
                      </div>
                    );
                  })}
                </CButtonToolbar>
              )}

              <div style={{ display: "inline-block" }}>
                <SFormSelect
                  md={4}
                  sm={12}
                  label={false}
                  id="color"
                  options={["black", "white", "walnet", "Natural"]}
                  change={onInputChangeHandler}
                />
              </div>
            </CCol>
          </CRow>
        </CCol>
        <CCol md={6} sm={12}></CCol>
      </CRow>
    </Form>
  );
};
export default EditProduct;
