import { CCol, CRow } from "@coreui/react";
import { Form } from "react-bootstrap";
import CButton from "src/Components/UI/Button/Button";
import { useEffect, useState } from "react";
import Card from "src/Components/UI/Card/Card";
import FormInputLabel from "src/Components/UI/Input/FormInputLabel";
import useFormValidate from "src/Hooks/input-validation";
const Job = (props) => {
  const data = props.body;
  // console.log(data);
 const [job, setstate] = useState(initialState)

  useEffect(() => {
    console.log("data", data.job_title);
    setJobTitle(data.job_title);
  }, [setJobTitle, data]);
  return (
    <Card color="primary" header="Job Information">
      <Form>
        <CRow>
          <FormInputLabel
            label="Job Title"
            type="text"
            readOnly={true}
            md={12}
            sm={12}
            value={jobTitle}
            change={jobTitleChangeHandler}
          />
        </CRow>
        <CRow>
          <FormInputLabel
            label="Joined Date"
            type="text"
            readOnly={true}
            md={6}
            sm={12}
          />
          <FormInputLabel
            label="Job Type"
            type="text"
            readOnly={true}
            md={6}
            sm={12}
          />
        </CRow>
      </Form>
    </Card>
  );
};
export default Job;
