import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isLoading: false,
  loggedInData: {
    userName: "",
    name: "",
    token: null,
    role_id: 0,
  },
};

const loginSlice = createSlice({
  name: "login",
  initialState,
  reducers: {},
});
export default loginSlice;
