import { useEffect, useState } from "react";
import { CCol, CContainer, CRow } from "@coreui/react";
import { useSelector } from "react-redux";
import classes from "./EditOrder.module.css";
import { getDate } from "src/custom-functions";
import DetailItem from "../UI/detailItem";
const EditOrder = () => {
  const mapStateToProps = (state) => {
    return {
      data: state.shopStore.singleOrderData.data,
    };
  };
  const state = useSelector(mapStateToProps);

  //   useEffect(() => {}, [state.data]);
  console.log(state.data);
  return (
    <CContainer>
      <CRow>
        <CCol
          md={8}
          sm={12}
          style={{ backgroundColor: "green", width: "100%" }}
        ></CCol>
        <CCol md={4} sm={12} style={{ width: "100%" }}>
          <div className={classes.lineBreak}>
            <h5>Customer Node</h5>
            <div className={classes.note}>
              {state.data.note ? (
                state.data.note
              ) : (
                <span>No Notes from Customer</span>
              )}
            </div>
          </div>
          <div>
            <h5>Customer Details</h5>
            <div className={classes.customerInfo}>
              <div className={classes.heading}>
                <h5>Customer Name</h5>
              </div>
              <div
                className={classes.font}
              >{`${state.data.customer_details.firstName} ${state.data.customer_details.lastName}`}</div>
            </div>
          </div>
          <div className={classes.contactInfo}>
            <div className={classes.heading}>
              <h5>Contact Information</h5>
            </div>
            <div
              className={classes.font}
            >{`${state.data.customer_details.email}`}</div>
            <div
              className={classes.font}
            >{`${state.data.customer_details.phone}`}</div>
          </div>
          <div className={classes.contactInfo}>
            <div className={classes.heading}>
              <h5>Billing Address</h5>
            </div>
            <DetailItem className={classes.font} />
            <div
              className={classes.font}
            >{`${state.data.customer_details.lineOne}`}</div>
            <div
              className={classes.font}
            >{`${state.data.customer_details.town}`}</div>
            <div
              className={classes.font}
            >{`${state.data.customer_details.zip} ${state.data.customer_details.state}`}</div>
            <div
              className={classes.font}
            >{`${state.data.customer_details.country}`}</div>
          </div>
          <div className={classes.contactInfo}>
            <div className={classes.heading}>
              <h5>Shipping Address</h5>
            </div>
            <div
              className={classes.font}
            >{`Same as Billing Address`}</div>
           
          </div>
          <div className={classes.contactInfo}>
            <div className={classes.heading}>
              <h5>Order Creation Date</h5>
            </div>
            <div
              className={classes.font}
            >{`${getDate(state.data.created_at)}`}</div>
           
          </div>
        </CCol>
      </CRow>
    </CContainer>
  );
};
export default EditOrder;
